import React, {Component} from 'react';
import './App.css';
import Navigation from './components/Navigation/Navigation';
import ImageLinkForm from './components/ImageLinkForm/ImageLinkForm';
import Detector from './components/Detector/Detector';
import Logo from './components/Logo/Logo';
import Rank from './components/Rank/Rank';
import Signin from './components/Signin/Signin';
import Particles from 'react-particles-js';
import Clarifai from 'clarifai'

const app = new Clarifai.App({
 apiKey: 'c9905c0df18447da84c6dbd96cfdb237'
});

app.inputs.create({
  url: "https://i.pinimg.com/originals/6c/a1/e2/6ca1e2111625766bf43db1b0a0c86629.jpg",
  concepts: [
    {
      id: "corgi",
      value: true
    }
  ]
});

app.models.create(
  "breeds",
  [
    { "id": "corgi" }
  ]
).then(
  function(response) {
    // do something with response

  },
  function(err) {
    // there was an error
  }
);

// app.models.train("{model_id}").then(
//   function(response) {
//     // do something with response
//   },
//   function(err) {
//     // there was an error
//   }
// );

const particleOptions = {
  particles: {
    number: {
      value:100,
      density: {
        enable:true,
        value_area:800
      }
    }
  }
}

class App extends Component{
  constructor() {
    super();
    this.state = {
      input: '',
      imageUrl: '',
      box: {},
      route: 'signin'
    }
  }

  locateFaceLocation = (data) => {
    const faceBox = data.outputs[0].data.regions[0].region_info.bounding_box
    const image = document.getElementById('input_image')
    const width = Number(image.width)
    const height = Number(image.height)
    return {
      leftCol: faceBox.left_col * width,
      topRow: faceBox.top_row * height,
      rightCol: width - (faceBox.right_col * width),
      bottomRow: height - (faceBox.bottom_row * height),
    }
  }

  displayBox = (box) => {
    this.setState({box: box})
  }

  onInputChange = (event) => {
    this.setState({input: event.target.value});
  }

  onSubmit = (event) => {
    this.setState({imageUrl: this.state.input})
    // app.models.predict(
    //   "a403429f2ddf4b49b307e318f00e528b", 
    //   this.state.input)
    // .then(response => this.displayBox(this.locateFaceLocation(response)))
    // .catch(err => console.log(err));
    app.models.predict({id:'MODEL_ID', version:'MODEL_VERSION_ID'}, "https://samples.clarifai.com/metro-north.jpg").then(
      function(response) {
        // do something with response
      },
      function(err) {
        // there was an error
      }
    );
  }

  render() {
    return (
      <div className="App">
        <Particles className='particles' params={particleOptions}/>
        <Signin/>
        <Navigation/>
        <Logo/>
        <Rank/>
        <ImageLinkForm onInputChange={this.onInputChange} onSubmit={this.onSubmit}/>
        <Detector box={this.state.box} imageUrl={this.state.imageUrl}/>
      </div>
    )
  }
}

export default App;
